sys_script_fix.script:
var gr = new GlideRecord('incident');

gr.addEncodedQuery('short_descriptionISNOTEMPTY^descriptionISNOTEMPTY');
gr.setLimit(1); //just for testing
gr.query();
var newDesc = gr.description + '\n\n' + gr.short_description;
gr.setValue('description', newDesc);
gr.setValue('short_description', 'Appended to OOB Description field'); //just for testing
gr.updateMultiple();

gr.addEncodedQuery('short_descriptionISNOTEMPTY^descriptionISEMPTY');
gr.setLimit(1); //just for testing
gr.query();
var newDesc = gr.short_description;
gr.setValue('description', newDesc);
gr.setValue('short_description', 'Moved to OOB Description field'); //just for testing
gr.updateMultiple();
